import React, { useState } from "react";
import moment from "moment";
import { range } from "moment-range";
import "../components/calendar.css";

export default function MainIndex(props) {
  const [showCalendarTable, setShowCalenderTable] = useState(true);
  const [dateObject, setDateObject] = useState(moment());

  const [showMonthTable, setShowMonthTable] = useState(false);

  const [allmonths, setAllmonths] = useState(moment.months());
  const [showYearNav, setShowYearNav] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);

  let weekdayshort = moment.weekdaysShort();

  const daysInMonth = () => {
    return dateObject.daysInMonth();
  };
  const year = () => {
    return dateObject.format("Y");
  };
  const currentDay = () => {
    return dateObject.format("D");
  };
  const firstDayOfMonth = () => {
    let dateObject = dateObject;
    let firstDay = moment(dateObject)
      .startOf("month")
      .format("d"); // Day of week 0...1..5...6
    return firstDay;
  };
  const month = () => {
    return dateObject.format("MMMM");
  };
  const showMonth = (e, month) => {
    this.setState({
      showMonthTable: !showMonthTable,
      showCalendarTable: !showCalendarTable,
    });
  };
  const setMonth = (month) => {
    let monthNo = allmonths.indexOf(month);
    let dateObject = Object.assign({}, dateObject);
    dateObject = moment(dateObject).set("month", monthNo);
    this.setState({
      dateObject: dateObject,
      showMonthTable: !showMonthTable,
      showCalendarTable: !showCalendarTable,
    });
  };
  const MonthList = (props) => {
    let months = [];
    props.data.map((data) => {
      months.push(
        <td
          key={data}
          className="calendar-month"
          onClick={(e) => {
            setMonth(data);
          }}
        >
          <span>{data}</span>
        </td>
      );
    });
    let rows = [];
    let cells = [];

    months.forEach((row, i) => {
      if (i % 3 !== 0 || i == 0) {
        cells.push(row);
      } else {
        rows.push(cells);
        cells = [];
        cells.push(row);
      }
    });
    rows.push(cells);
    let monthlist = rows.map((d, i) => {
      return <tr>{d}</tr>;
    });

    return (
      <table className="calendar-month">
        <thead>
          <tr>
            <th colSpan="4">Select a Month</th>
          </tr>
        </thead>
        <tbody>{monthlist}</tbody>
      </table>
    );
  };

  const showYearEditor = () => {
    this.setState({
      showYearNav: true,
      showCalendarTable: !showCalendarTable,
    });
  };

  const onPrev = () => {
    let curr = "";
    if (showMonthTable == true) {
      curr = "year";
    } else {
      curr = "month";
    }
    this.setState({
      dateObject: dateObject.subtract(1, curr),
    });
  };
  const onNext = () => {
    let curr = "";
    if (showMonthTable == true) {
      curr = "year";
    } else {
      curr = "month";
    }
    this.setState({
      dateObject: dateObject.add(1, curr),
    });
  };
  const setYear = (year) => {
    // alert(year)
    let dateObject = Object.assign({}, dateObject);
    dateObject = moment(dateObject).set("year", year);
    this.setState({
      dateObject: dateObject,
      showMonthTable: !showMonthTable,
      showYearNav: !showYearNav,
      showMonthTable: !showMonthTable,
    });
  };
  const onYearChange = (e) => {
    this.setYear(e.target.value);
  };
  const getDates = (startDate, stopDate) => {
    var dateArray = [];
    var currentDate = moment(startDate);
    var stopDate = moment(stopDate);
    while (currentDate <= stopDate) {
      dateArray.push(moment(currentDate).format("YYYY"));
      currentDate = moment(currentDate).add(1, "year");
    }
    return dateArray;
  };
  const YearTable = (props) => {
    let months = [];
    let nextten = moment()
      .set("year", props)
      .add("year", 12)
      .format("Y");

    let tenyear = getDates(props, nextten);

    tenyear.map((data) => {
      months.push(
        <td
          key={data}
          className="calendar-month"
          onClick={(e) => {
            this.setYear(data);
          }}
        >
          <span>{data}</span>
        </td>
      );
    });
    let rows = [];
    let cells = [];

    months.forEach((row, i) => {
      if (i % 3 !== 0 || i == 0) {
        cells.push(row);
      } else {
        rows.push(cells);
        cells = [];
        cells.push(row);
      }
    });
    rows.push(cells);
    let yearlist = rows.map((d, i) => {
      return <tr>{d}</tr>;
    });

    return (
      <table className="calendar-month">
        <thead>
          <tr>
            <th colSpan="4">Select a Yeah</th>
          </tr>
        </thead>
        <tbody>{yearlist}</tbody>
      </table>
    );
  };
  const onDayClick = (e, d) => {
    this.setState(
      {
        selectedDay: d,
      },
      () => {
        console.log("SELECTED DAY: ", selectedDay);
      }
    );
  };
  let weekdayshortname = this.weekdayshort.map((day) => {
    return <th key={day}>{day}</th>;
  });
  let blanks = [];
  for (let i = 0; i < this.firstDayOfMonth(); i++) {
    blanks.push(<td className="calendar-day empty">{""}</td>);
  }
  let daysMonth = [];
  for (let d = 1; d <= this.daysMonth(); d++) {
    let currentDay = d == this.currentDay() ? "today" : "";
    // let selectedClass = (d == selectedDay ? " selected-day " : "")
    daysMonth.push(
      <td key={d} className={`calendar-day ${currentDay}`}>
        <span
          onClick={(e) => {
            this.onDayClick(e, d);
          }}
        >
          {d}
        </span>
      </td>
    );
  }
  var totalSlots = [...blanks, ...daysMonth];
  let rows = [];
  let cells = [];

  totalSlots.forEach((row, i) => {
    if (i % 7 !== 0) {
      cells.push(row);
    } else {
      rows.push(cells);
      cells = [];
      cells.push(row);
    }
    if (i === totalSlots.length - 1) {
      // let insertRow = cells.slice();
      rows.push(cells);
    }
  });

  let daysinmonth = rows.map((d, i) => {
    return <tr>{d}</tr>;
  });

  return (
    <>
      <div className="tail-datetime-calendar">
        <div className="calendar-navi">
          <span
            onClick={(e) => {
              this.onPrev();
            }}
            class="calendar-button button-prev"
          />
          {!this.state.showMonthTable && !this.state.showYearEditor && (
            <span
              onClick={(e) => {
                this.showMonth();
              }}
              class="calendar-label"
            >
              {this.month()},
            </span>
          )}
          <span
            className="calendar-label"
            onClick={(e) => {
              this.showYearEditor();
            }}
          >
            {this.year()}
          </span>

          <span
            onClick={(e) => {
              this.onNext();
            }}
            className="calendar-button button-next"
          />
        </div>
        <div className="calendar-date">
          {this.state.showYearNav && <this.YearTable props={this.year()} />}
          {this.state.showMonthTable && (
            <this.MonthList data={moment.months()} />
          )}
        </div>

        {this.state.showCalendarTable && (
          <div className="calendar-date">
            <table className="calendar-day">
              <thead>
                <tr>{weekdayshortname}</tr>
              </thead>
              <tbody>{daysinmonth}</tbody>
            </table>
          </div>
        )}
      </div>
    </>
  );
}
