import React from "react";
import moment from "moment";
import { range } from "moment-range";

export default function DisplayCalender(props) {
  const {
    onPrev,
    onNext,
    showMonth,
    daysinmonth,
    dateObject,
    showMonthTable,
    showYearEditor,
    showYearNav,
    showCalendarTable,
    setYear
  } = props;
  const year = () => {
    return dateObject.format("Y");
  };
  let weekdayshort = moment.weekdaysShort();
  const month = () => {
    return dateObject.format("MMMM");
  };

  let weekdayshortname = weekdayshort.map((day) => {
    return <th key={day}>{day}</th>;
  });

  let YearTable = (props) => {
    console.log(props);
    let months = [];
    let nextten = moment()
      .set("year", "2009")
      .add("year", 35)
      .format("Y");

    let tenyear = this.getDates("2008", nextten);

    tenyear.map((data) => {
      months.push(
        <td
          key={data}
          className="calendar-month"
          onClick={(e) => {
            setYear(data);
          }}
        >
          <span>{data}</span>
        </td>
      );
    });
    let rows = [];
    let cells = [];

    months.forEach((row, i) => {
      if (i % 3 !== 0 || i == 0) {
        cells.push(row);
      } else {
        rows.push(cells);
        cells = [];
        cells.push(row);
      }
    });
    rows.push(cells);
    let yearlist = rows.map((d, i) => {
      return <tr>{d}</tr>;
    });

    return (
      <table className="calendar-month">
        <thead>
          <tr>
            <th colSpan="4">Select a Yeah</th>
          </tr>
        </thead>
        <tbody>{yearlist}</tbody>
      </table>
    );
  };

  return (
    <>
      <div className="tail-datetime-calendar">
        <div className="calendar-navi">
          <span
            onClick={(e) => {
              onPrev();
            }}
            class="calendar-button button-prev"
          />
          {!showMonthTable && !showYearEditor && (
            <span
              onClick={(e) => {
                showMonth();
              }}
              class="calendar-label"
            >
              {month()},
            </span>
          )}
          <span
            className="calendar-label"
            onClick={(e) => {
              this.showYearEditor();
            }}
          >
            {year()}
          </span>

          <span
            onClick={(e) => {
              onNext();
            }}
            className="calendar-button button-next"
          />
        </div>
        <div className="calendar-date">
          {showYearNav && <YearTable props={year()} />}
          {showMonthTable && <this.MonthList data={moment.months()} />}
        </div>

        {showCalendarTable && (
          <div className="calendar-date">
            <table className="calendar-day">
              <thead>
                <tr>{weekdayshortname}</tr>
              </thead>
              <tbody>{daysinmonth}</tbody>
            </table>
          </div>
        )}

        <div>
          <button onClick={this.handleToday}>Today</button>
        </div>
      </div>
    </>
  );
}
